SELECT P.RefNumber, D.ItemRef_FullName, D.Quantity, D.Rate, B.ItemRef_FullName, B.Quantity, B.Rate FROM pedidos as P INNER JOIN pedidosdetalle as D ON P.TxnID = D.IDKEY INNER JOIN bonificadetalle AS B ON P.TxnID = B.IDKEY WHERE P.RefNumber = '7360'
SELECT I.TxnNumber, I.CustomerRef_ListID, I.CustomerRef_FullName, I.TxnDate, I.RefNumber, I.Subtotal, I.SalesTaxTotal, I.CustomField2, D.ItemRef_ListID, D.ItemRef_FullName, D.Description, D.Quantity, D.Rate, D.Amount, D.IDKEY, C.Name, C.CustomField2 FROM invoice AS I INNER JOIN invoicelinedetail AS D ON I.TxnID = D.IDKEY INNER JOIN customer AS C ON I.CustomerRef_ListID = C.ListID WHERE I.RefNumber = '1-1-7535'